import React from 'react';
import Link from "./Link.js";


class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {birthdate: ''};

        this.validateBirthdate = this.validateBirthdate.bind(this);
        this.handleSubmit = this;
        this.openTermAndConditions = this;
    }

    validateBirthdate(event) {
        console.log(event.target.value);
        // TODO 1992-08-27
        //TODO Först 4 SIFFROR automatiskt bindestäck, 2 siffror, bindesträck 2 siffor. Max 10 chars.
    }

    handleSubmit(event) {
        console.log("REGISTER");
        event.preventDefault();
    }

    openTermAndConditions() {
        console.log("Term and conditions");
    }

    render() {
        return (
            <div className="register_container">
                <form className="form_register" method="" onSubmit={handleSubmit}>
                    <label className="input_label">
                        Name
                        <input name="name" required type="text"/>
                    </label>

                    <label className="input_label">
                        Email
                        <input name="email" required type="email"/>
                    </label>

                    <label className="input_label">
                        Password
                        <input name="password" required type="password"/>
                    </label>

                    <div className="date_picker_container">
                        Birthdate
                        <label className="input_label">
                            <input required name="year" type="number" value={this.state.birthdate}
                                   onChange={this.validateBirthdate}/>
                        </label>
                    </div>
                    <div className="terms_and_conditions_text">
                        <label>
                            I accept <a onClick={openTermAndConditions} className="form_link"> Terms and Conditions</a>
                        </label>
                        <input name="gdpr" required type="checkbox"/>
                    </div>


                    <input name="register" type="Submit" defaultValue="Register" className="button_submit"/>

                </form>
            </div>
        )
    }
}


export default Register;
