import React from 'react';


class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {birthdate: '',
                        invalid: false};

        this.validateBirthdate = this.validateBirthdate.bind(this);
    }

    validateBirthdate(event) {
        var value = event.target.value;
        if (value.length < this.state.birthdate.length) {

        } else if (value.length === 4) {
            value += "-";
        } else if (value.length === 7) {

            console.log();
            if(value.slice(-2) > 12){
                this.stat;
            }
            value += "-";

        }
        this.setState({birthdate: value});

    }


    render() {
        return (
            <div className="register_container">
                <form className="form_register" method="" onSubmit={handleSubmit}>
                    <label className="input_label">
                        Name
                        <input name="name" required type="text"/>
                    </label>

                    <label className="input_label">
                        Email
                        <input name="email" required type="email"/>
                    </label>

                    <label className="input_label">
                        Password
                        <input name="password" required type="password"/>
                    </label>

                    <div className="date_picker_container">

                        <label className="input_label">
                            <span>Birthdate </span><span className="small_description_text">1900-01-01 (Y-M-D)</span>
                            <input type="year" required name="year" value={this.state.birthdate}
                                   onChange={this.validateBirthdate} maxLength="10"/>
                        </label>
                    </div>
                    <div className="terms_and_conditions_text">
                        <label>
                            I accept <a href="/register" onClick={openTermAndConditions} className="form_link"> Terms
                            and Conditions</a>
                        </label>
                        <input name="gdpr" required type="checkbox"/>
                    </div>


                    <input name="register" type="Submit" defaultValue="Register" className="button_submit"/>

                </form>
            </div>
        )
    }
}

function handleSubmit(event) {
    console.log("REGISTER");
    event.preventDefault();
}

function openTermAndConditions(event) {
    console.log("Term and conditions");
    event.preventDefault();
}

export default Register;
