import React from 'react';
import ReportQuestion from './ReportQuestion.js';

class ReportEdit extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            kmom: this.props.match.params.kmom,
            reportData: {data: []},
            editField: [],
        };
        this.getReport();
    }

    getReport = () => {
        fetch("http://localhost:8333/reports/" + this.state.kmom, {
            method: "GET",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
        }).then((res) => {
            return res.json();
        }).then(async (res) => {
            console.log(res);
            this.setState({reportData: JSON.parse(res.data.text)});
            this.state.editField = this.createReportQuestion(this.state.reportData.data);
        });
    };
    postReport = (data) => {
        console.log(this.state.reportData);
        // todo skicka json filer
        fetch("http://localhost:8333/reports", {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                kmom: this.state.kmom,
                data: data
            })
        }).then((res) => {
            return res.json()
        }).then((res) => {
            console.log(res);
        })

    };

    createReportQuestion = (data) => {
        var questions = data.map((collection, index) => {
            index += this.state.editField.length;
            return (
                <ReportQuestion
                    key={index}
                    index={index}
                    question={collection.question}
                    answer={collection.answer}
                    triggerQuestion={this.handleOnChange}
                    triggerAnswer={this.handleOnChange}
                />
            );
        });
        var newData = this.state.editField;
        newData.push(questions);
        this.setState({editField: newData});
    };
    addQuestion = (event) => {
        if (event) {
            event.preventDefault();
        }

        this.createReportQuestion([{question: "New", answer: "New"}])
        console.log(this.state.editField);
    };

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
        //this.postReport(this.state.reportData);
    };

    handleOnChange = (event) => {
        console.log(event.target.name);
        let nam = event.target.name;
        let val = event.target.value;
        var state = this.state.reportData;
        state[nam] = val;
        this.setState({reportData: state});
    };


    render() {
        return (
            <div className="edit_report_container">
                <div>
                    {this.state.editField}
                </div>
                <form className="edit-question-form" onSubmit={this.handleSubmit}>
                    <button onClick={this.addQuestion}>Add question</button>
                    <input type="submit" value="Spara"/>
                </form>

                <div>
                </div>
            </div>

        )
    }
}

export default ReportEdit;
