import React from 'react';


class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            birthdate: '',
            errorMessage: false,
            year: 0,
            month: 0,
            day: 0
        };

    }

    validateMonth = (event) => {
        console.log(event.target.value);
        this.setState({birthdate: event.target.value})
    }

    validateBirthdate = (event) => {
        var value = event.target.value;
        /*
         * If erase, do nothing.
         * After 4 & 7 digits, add -
         * Check how many days the months has and se if entered days is OK.
         */
        if (value.length < this.state.birthdate.length) {

        } else if (value.length === 4) {
            this.setState({year: value});
            value += "-";
        } else if (value.length === 7) {
            value = this.val(value);
        } else if (value.length === 10) {
            var day = value.slice(-2);
            this.setState({day: day});
            var maxDays = numberOfDays(this.state.month, this.state.year);

            if (day > maxDays || day === '00') {
                var mon = value.slice(-2);
                this.setState({month: mon});
                if (mon > 12 || mon === '00') {
                    value = value.slice(0, -2);
                    this.setState({errorMessage: "Invalid Month"});
                } else {
                    value += "-";
                }
                this.setState({errorMessage: "Invalid day"});
            } else {
                this.setState({errorMessage: false});
            }
        }
        this.setState({birthdate: value});
    }


    render() {
        return (
            <div className="register_container">
                <form className="form_register" method="" onSubmit={handleSubmit}>
                    <label className="input_label">
                        Name
                        <input name="name" required type="text"/>
                    </label>

                    <label className="input_label">
                        Email
                        <input name="email" required type="email"/>
                    </label>

                    <label className="input_label">
                        Password
                        <input name="password" required type="password"/>
                    </label>

                    <div className="date_picker_container">

                        <label className="input_label">
                            <span>Birthdate </span><span
                            className="small_description_text">e.g. 1900-01-01 (Y-M-D)</span>
                            <input type="year" required name="birthdate" value={this.state.birthdate}
                                   onChange={this.val} maxLength="10"/>
                            <p className="error_message">{this.state.errorMessage} </p>
                        </label>
                    </div>
                    <div className="terms_and_conditions_text">
                        <label>
                            I accept <a href="/register" onClick={openTermAndConditions} className="form_link"> Terms
                            and Conditions</a>
                        </label>
                        <input name="gdpr" required type="checkbox"/>
                    </div>


                    <input name="register" type="Submit" defaultValue="Register" className="button_submit"/>

                </form>
            </div>
        )
    }
}

/*
 * Return number of days a month has.
 * @ month 01-12 string.
 * @ year string as number
 */
function numberOfDays(month, year) {
    var mon = month % 2;
    if (month === '02') {
        if (calculateLeapYear(year)) {
            return 29;
        }
        return 28;
    } else if (mon === 0 || month === '07') {
        return 31;
    }
    return 30;
}

function calculateLeapYear(year) {
    if (year % 4 === 0) {
        if (year % 400 === 0) {
            return true;
        } else if (year % 100 === 0) {
            return false;
        }
        return true;
    }
    return false;
}

function handleSubmit(event) {
    console.log("REGISTER");
    event.preventDefault();
}

function openTermAndConditions(event) {
    console.log("Term and conditions");
    event.preventDefault();
}

export default Register;
