import React from 'react';
import ReportQuestion from './ReportQuestion.js';

class ReportEdit extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            qas: testData
        };
    }

    getReport = () => {
        const kmom = this.props.match.params.kmom;
        fetch("http://localhost:8333/reports/" + kmom, {
            method: "GET",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
        }).then((res) => {
            return res.json()
        }).then((res) => {
            console.log(res);

        });
    };
    postReport = (event) => {
        console.log(this.state.qas);
        // todo skicka json filer
        fetch("http://localhost:8333/reports", {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                email: "this.state.email",
                password: "this.state.password"
            })
        }).then((res) => {
            return res.json()
        }).then((res)=>{
            console.log(res);
        })

    };

    createReportQuestion = (data) => {
        var questions = data.map((collection, index) => {
            return (
                <ReportQuestion
                    key={index}
                    index={index}
                    question={collection.question}
                    answer={collection.answer}
                    trigger={this.handleOnChange}
                />
            );
        });

        return questions;
    };
    addQuestion = (event) =>{
        event.preventDefault();
        var newQas = this.state.qas;
        newQas.push({question: "New", answer: "New"});
        this.setState({qas: newQas});
    };

    handleSubmit = (event) => {
        event.preventDefault();
        this.postReport();
    };

    handleOnChange = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    };


    render() {
        const kmom = this.props.match.params.kmom;
        this.getReport(kmom);
        const questions = this.createReportQuestion(this.state.qas);
        return (

            <div className="edit_report_container">
                <div>
                    {this.test}
                </div>
                <form className="edit-question-form" onSubmit={this.handleSubmit}>
                    {questions}
                    <button onClick={this.addQuestion} >Add question</button>
                    <input type="submit" value="Spara"/>
                </form>

                <div>
                </div>
            </div>

        )
    }
}


var testData = {
    "qas": [
        {
            question: "q1 alot of text asjdlkasjdlkas jdlaksjd lkasjdlk asjdlkasj ldkasjdlk ajslkdjaskl djaslk jlkadjskaljd ks klsjdlka jlkas jlkasj dlkasj dlkasjdl kasjd lkasj dlkasjd aslkdjsalkdj laskdjlaskjdlkasjd lkasjdlkasjdlkasj",
            answer: "a1"
        },
        {question: "q2", answer: "a2"},
        {question: "q3", answer: "a3"},
        {question: "q4", answer: "a4"},
        {question: "q5", answer: "a5"},
    ]

};

export default ReportEdit;
