import React from 'react';

class ReportQuestion extends React.Component {
    constructor(props) {
        super(props);

    }


    render() {
        return (
            <div className="question_input_container">
                <label >
                    Question
                    <textarea
                        name={"question"+ this.props.index}
                        type="textarea"
                        onChange={this.props.trigger}
                        defaultValue={this.props.question}

                    />
                </label>
                <label>
                    Answer
                    <textarea
                        name={"answer"+ this.props.index}
                        type="textarea"
                        defaultValue={this.props.answer}
                        onChange={this.props.trigger}

                    />
                </label>
            </div>

        )
    }
}


export default ReportQuestion;
