import React from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3000');

class Chat extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            text: "",
            username: "",
            messages: []    
        };

        this.handleChangeUsername = this.handleChangeUsername.bind(this);
        this.handleChangeText = this.handleChangeText.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleDataRecived = this.handleDataRecived.bind(this);
    }
    componentDidMount(){
        socket.on('connect', function() {
            console.info("Connected");
        });

        socket.on('chat message', this.handleDataRecived);

        socket.on('disconnect', function() {
            console.info("Disconnected");
        });
    }

    handleDataRecived(message) {
        var messages = this.state.messages;
        messages.push(message);
        this.setState({messages:messages});
    }

    handleSubmit(event) {
        event.preventDefault();

        socket.emit('chat message', 
        {
            username: this.state.username,
            text: this.state.text
        });
    }

    handleChangeUsername(event) {
         this.setState({username: event.target.value});
      }
    handleChangeText(event) {
        this.setState({text: event.target.value});
    }


    render() {
        return  <div className="chat-container">
                    <h4>Chat</h4>
                    <div className="all-messages">
                    {this.state.messages.map((msg, index) => (
                        <div key={index} className="message">
                            <p>  {msg.text}</p>
                            <p> {msg.username}</p>
                        </div>

                    ))}

                    </div>

                    <form onSubmit={this.handleSubmit} className="message-form">
                        <input type="text" className="form-username" onChange={this.handleChangeUsername}/>
                        <input type="text" className="form-text" onChange={this.handleChangeText} />
                        <input type="submit" value="Skicka" />
                    </form>

                </div>
    }
}






export default Chat;
