import React from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3000');

socket.on('connect', function() {
    console.info("Connected");
    console.info("Connected s2123");
});

socket.on('disconnect', function() {
    console.info("Disconnected");
});

const newMessage = document.getElementById("new-message");

socket.on('connect', function() {
    newMessage.addEventListener("keyup", function (event) {
        if (event.code === "Enter") {
            socket.emit('chat message', event.target.value);
            event.target.value = "";
        }
    });
});


class Chat extends React.Component {
    render() {
        return  <div className="chat-container">
                    <h4>Chat</h4>
                    <div className="all-message"></div>

                    <p><strong>Write new message:</strong></p>
                    <input id="new-message" class="new-message"/>
                </div>
    }
}

export default Chat;
