import React from 'react';
import ReportQuestion from './ReportQuestion.js';

class ReportEdit extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            reportData: {qas: []},
            displayData: {qas: []}
        };

    }

    getReport = () => {
        const kmom = this.props.match.params.kmom;
        fetch("http://localhost:8333/reports/" + kmom, {
            method: "GET",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
        }).then((res) => {
            return res.json()
        }).then((res) => {
            if(res.data){
                this.state.displayData = res.data;
            }
            else{
               this.addQuestion();
            }

        });
    };
    postReport = (data) => {
        console.log(this.state.reportData);
        // todo skicka json filer
        fetch("http://localhost:8333/reports", {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                reportData: data
            })
        }).then((res) => {
            return res.json()
        }).then((res) => {
            console.log(res);
        })

    };

    createReportQuestion = (data) => {
        var questions = data.map((collection, index) => {
            return (
                <ReportQuestion
                    key={index}
                    index={index}
                    question={collection.question}
                    answer={collection.answer}
                    trigger={this.handleOnChange}
                />
            );
        });

        return questions;
    };
    addQuestion = (event) => {
        event.preventDefault();
        var newData = this.state.displayData;
        console.log(newData);
        newData.qas.push({question: "New", answer: "New"});
        this.setState({displayData: newData});
    };

    handleSubmit = (event) => {
        event.preventDefault();

        this.postReport(this.state.reportData);
    };

    handleOnChange = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    };


    render() {
        this.getReport();
        return (
            <div className="edit_report_container">
                <div>
                    {this.state.displayData.qas}
                </div>
                <form className="edit-question-form" onSubmit={this.handleSubmit}>
                    <button onClick={this.addQuestion}>Add question</button>
                    <input type="submit" value="Spara"/>
                </form>

                <div>
                </div>
            </div>

        )
    }
}

export default ReportEdit;
