import React from 'react';
import {HeaderReports} from './Header.js';



class Report extends React.Component {
    render() {
        const kmom = match.params.kmom;
        const [questions, setQuestions] = useState([]);

        useEffect(() => {
            fetch(`https://me-api.jsramverk.me/reports/${kmom}`)
                .then(res => res.json())
                .then(res => setQuestions(res.data));
        });

        const QuestionsList = () =>
            questions.map((question, index) => (
                <div className="question" key={index}>
                    <p>
                        <strong>{question.question}</strong>
                    </p>
                    <p>{question.answer}</p>
                </div>
            ));

        return (

            <article className="article">
                <div className="article_header">
                    <h2>REPORT PAGE</h2>
                    <HeaderReports/>
                </div>

                <div className="article_main_content">
                    <Kmom kmom={kmomOne}/>
                </div>

            </article>
        )
    }
}


class Kmom extends React.Component {

    render() {
        return (
            <div className="report_kmom">
                <h5>{}</h5>
            </div>
        )
    }
}
var kmomOne = [
    [
        "Github",
        "Starta appen",
        "Installera Moduler"
    ],
    [
        "https://github.com/JohanLe/bth-ramverk1",
        "I en node miljö:\n" +
        "\n" +
        "Installera Serve: \"npm install -g serve\"\n" +
        "Stå i me-app mappen och använd: \"serve -s build\"\n" +
        "Öppna webbläsaren på 'localhost:5000' eller vald port.",
        "Först installera Node med npm.\n" +
        "\n" +
        "Modulerna installers via npm och npx.\n" +
        "\n" +
        "Bootstrapa projektet med\n" +
        "\n" +
        "\"npx create-react-app my-app-name\"\n" +
        "Moduler till detta projekt\n" +
        "\n" +
        "Stå i projekt mappen & \"npm install react-router-dom --save\"\n" +
        "\"npm install -g sass\""
    ]
]

export default Report;
