import React from 'react';
import ReportQuestion from './ReportQuestion.js';

class ReportEdit extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            kmom: this.props.match.params.kmom,
            reportData: [],
            editFields: [],
        };
        this.getReport();
    }

    getReport = () => {
        fetch("http://localhost:8333/reports/" + this.state.kmom, {
            method: "GET",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
        }).then((res) => {
            return res.json();
        }).then(async (res) => {
            this.setState({reportData: JSON.parse(res.data.text).data});
            this.state.editFields = this.createReportQuestion(this.state.reportData);
            console.log("REPORTDATA")
            console.log(this.state.reportData);
        });
    };
    postReport = (data) => {
        console.log(this.state.reportData);
        // todo skicka json filer
        fetch("http://localhost:8333/reports", {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                kmom: this.state.kmom,
                data: data
            })
        }).then((res) => {
            return res.json()
        }).then((res) => {
            console.log(res);
        })

    };

    createReportQuestion = (data) => {
        var questions = data.map((collection, index) => {
            index += this.state.editFields.length;
            return (
                <ReportQuestion
                    key={index}
                    index={index}
                    question={collection.question}
                    answer={collection.answer}
                    triggerQuestion={this.handleOnChange}
                    triggerAnswer={this.handleOnChange}
                />
            );
        });
        var newData = this.state.editFields;
        newData.push(questions);
        this.setState({editFields: newData});
    };
    addQuestion = (event) => {
        if (event) {
            event.preventDefault();
        }

        this.createReportQuestion([{id: (state.editFields.length + 1),question: "New", answer: "New"}])
        console.log(this.state.editFields);
    };

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(event.target[0]);
        console.log(event.target[1]);
        //this.postReport(this.state.reportData);
    };

    handleOnChange = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        var state = this.state.reportData;

        for (var i = 0; i < state.length; i++) {
            console.log(state[i]);
            if (state[i].answer === nam) {
                console.log("Includes");
            }
        }


        this.setState({reportData:  state});
    };


    render() {
        return (
            <div className="edit_report_container">

                <form className="edit-question-form" onSubmit={this.handleSubmit}>
                    <div>
                        {this.state.editFields}
                    </div>
                    <button onClick={this.addQuestion}>Add question</button>
                    <input type="submit" value="Spara"/>
                </form>

            </div>

        )
    }
}

export default ReportEdit;
